// Node module: apim-ui

'use strict';

angular.module('apim.styleguide')
  .directive('apimStyleguideDialogs', ['$log', function($log) {
    return {
      restrict: 'E',
      scope: {},
      templateUrl: 'apim/styleguide/partials/styleguide.dialogs.html',
      controller: function($scope, $rootScope, $modal) {

        /**
          * Open a confirmation dialog
          */
          $scope.confirmDialog = function() {
            $scope.confirmation = {
              titleKey: 'exampleConfirmDialogTitle',
              messageKey: 'exampleConfirmDialogMessage',
              confirmed: function() {
                $log.log('confirmed example delete');
              },
              rejected: function() {
                $log.log('canceled example delete');
              }
            };

            $modal.open({
              templateUrl: 'apim/partials/confirmation.html',
              windowTemplateUrl: 'apim/partials/main-template.html',
              controller: 'ConfirmationController',
              scope: $scope
            });
          };

        /**
          * Open an informational dialog
          */
          $scope.informationDialog = function(){
            $scope.information = {
              titleKey: 'exampleInformationDialogTitle',
              messageKey: 'exampleInformationDialogMessage',
              accepted: function() {
                $log.log('accepted');
              }
            };
            $modal.open({
              templateUrl: 'apim/partials/information.html',
              windowTemplateUrl: 'apim/partials/main-template.html',
              controller: 'InformationController',
              scope: $scope
            });
          }
      }
    }
  }])
  .directive('apimStyleguideButtons', ['$log', function($log) {
    return {
      restrict: 'E',
      scope: {},
      templateUrl: 'apim/styleguide/partials/styleguide.buttons.html',
      controller: function($scope, $rootScope) {
      }
    }
  }])
  .directive('apimStyleguideColors', ['$log', function($log) {
    return {
      restrict: 'E',
      scope: {},
      templateUrl: 'apim/styleguide/partials/styleguide.colors.html',
      controller: function($scope, $rootScope) {
      }
    }
  }])
  .directive('apimStyleguideForms', ['$log', function($log) {
    return {
      restrict: 'E',
      scope: {},
      templateUrl: 'apim/styleguide/partials/styleguide.forms.html',
      controller: function($scope, $rootScope) {
      }
    }
  }])
  .directive('apimStyleguideNavigation', ['$log', function($log) {
    return {
      restrict: 'E',
      scope: {},
      templateUrl: 'apim/styleguide/partials/styleguide.navigation.html',
      controller: function($scope, $rootScope) {
      }
    }
  }])
  .directive('apimStyleguideText', ['$log', function($log) {
    return {
      restrict: 'E',
      scope: {},
      templateUrl: 'apim/styleguide/partials/styleguide.text.html',
      controller: function($scope, $rootScope) {
      }
    }
  }]);

